mapper.select:
SELECT



FROM sys_department t join sys_department_user sdu on t.id=sdu.department_id
WHERE 1=1

    AND t.status=#{status}

AND sdu.user_id=#{userId}
ORDER BY t.lft

SELECT



FROM sys_department t
WHERE 1=1
AND t.parent_id=#{id}

    AND t.status=#{status}

ORDER BY t.lft

mapper.update:
UPDATE sys_department t,(SELECT * FROM sys_department WHERE id = #{id}) f SET t.lft= (t.lft + #{amount}), t.update_time=now() WHERE 1=1 AND t.is_update = 1
AND t.lft < #{range}
AND t.lft > f.rgt

UPDATE sys_department t,(SELECT * FROM sys_department WHERE id = #{id}) f SET t.rgt= (t.rgt + #{amount}), t.update_time=now() WHERE 1=1 AND t.is_update = 1
AND t.rgt < #{range}
AND t.rgt >= f.rgt

UPDATE sys_department t
SET t.is_update= #{isUpdate}
WHERE 1 = 1
AND t.id in

    #{id}
