mapper.select:
SELECT



FROM sys_resource t
WHERE 1=1

    AND t.type=#{type}


    AND t.status=#{status}

ORDER BY t.lft

SELECT



FROM sys_resource t
WHERE 1=1

    AND t.type=#{type}


    AND t.status=#{status}

AND t.id IN (
SELECT rr.resource_id
FROM sys_role_resource rr
JOIN (
SELECT ur.role_id AS role_id
FROM sys_user_role ur
WHERE ur.user_id =(
SELECT id AS userId
FROM sys_user u
WHERE u.username=#{username} OR u.id=#{userId}
)
) temp ON rr.role_id=temp.role_id
)
ORDER BY t.lft

SELECT
FROM sys_resource t WHERE 1=1
AND t.type=#{type}
AND t.status=#{status}
AND t.lft<( SELECT s.lft FROM sys_resource s WHERE s.id=#{id}) AND t.rgt>( SELECT s.lft FROM sys_resource s WHERE s.id=#{id})
ORDER BY t.lft

SELECT
FROM sys_resource t WHERE 1=1
AND t.type=#{type}
AND t.status=#{status}
AND t.lft>( SELECT s.lft FROM sys_resource s WHERE s.id=#{id}) AND t.rgt<( SELECT s.rgt FROM sys_resource s WHERE s.id=#{id})
ORDER BY t.lft

