mapper.update:
UPDATE sys_resource t,(SELECT * FROM sys_resource WHERE id = #{id, jdbcType=NUMERIC}) f SET t.lft= (t.lft + #{amount}), t.update_time=now() WHERE 1=1 AND t.is_update =1
AND t.lft < #{range}
AND t.lft > f.rgt

UPDATE sys_resource t,(SELECT * FROM sys_resource WHERE id = #{id, jdbcType=NUMERIC}) f SET t.rgt= (t.rgt + #{amount}), t.update_time=now() WHERE 1=1 AND t.is_update = 1
AND t.rgt < #{range}
AND t.rgt >= f.rgt

