mapper.select:
SELECT



FROM sys_resource t
WHERE 1=1

    AND t.type=#{type}


    AND t.status=#{status}

ORDER BY t.lft ASC

SELECT



FROM sys_resource t
WHERE 1=1

    AND t.type=#{type}


    AND t.status=#{status}

AND t.id IN (
SELECT rr.resource_id
FROM sys_role_resource rr
JOIN (
SELECT ur.role_id AS role_id
FROM sys_user_role ur
WHERE ur.user_id =(
SELECT id AS userId
FROM sys_user u
WHERE u.username=#{username} OR u.id=#{userId}
)
) temp ON rr.role_id=temp.role_id
)

SELECT
FROM sys_resource t WHERE 1=1
AND t.type=#{type}
AND t.status=#{status}
AND t.lft<( SELECT s.lft FROM sys_resource s WHERE s.id=#{id}) AND t.rgt>( SELECT s.lft FROM sys_resource s WHERE s.id=#{id})
ORDER BY t.lft ASC

SELECT
FROM sys_resource t WHERE 1=1
AND t.type=#{type}
AND t.status=#{status}
AND t.lft>( SELECT s.lft FROM sys_resource s WHERE s.id=#{id}) AND t.rgt<( SELECT s.rgt FROM sys_resource s WHERE s.id=#{id})
ORDER BY t.lft ASC

SELECT



FROM sys_resource t
WHERE 1=1
AND t.type=#{type}
AND t.parent_id=#{id}

    AND t.status=#{status}

ORDER BY t.lft ASC

mapper.update:
UPDATE sys_resource t,(SELECT * FROM sys_resource WHERE id = #{id, jdbcType=NUMERIC}) f SET t.lft= (t.lft + #{amount}), t.update_time=now() WHERE 1=1 AND t.is_update =1
AND t.lft > f.rgt
AND t.lft < #{range}

UPDATE sys_resource t,(SELECT * FROM sys_resource WHERE id = #{id, jdbcType=NUMERIC}) f SET t.rgt= (t.rgt + #{amount}), t.update_time=now() WHERE 1=1 AND t.is_update = 1
AND t.rgt > f.rgt
AND t.rgt < #{range}

UPDATE sys_resource t
SET t.is_update= #{isUpdate}
WHERE 1 = 1
AND t.id in

    #{id}

